import React from 'react';
import axios from 'axios';

class Blog extends React.Component {
  state = {
    posts: [],
    displayBlogBods: true
  };

  componentDidMount(){
    axios.get('http://localhost:8000/api/posts.json')
    .then(res => {
      const posts = res.data;
      this.setState({ posts });
    })
  }

  BlogBody(post){
	return (
		<div className='blog-bod'>
			<p>{ post.body }</p>
		</div>
	)
  }

  render() {
  	return (
  	  <div>
  	    <h1>Blog</h1>
  	    <ul>
        { this.state.posts.map(post =>
          <div>
            <li key={ post.id }> { post.title }: published { post.published }</li> 
            <p>{ post.body }</p>
          </div> ) }
        </ul>
  	  </div>	
  	)
  }

}

export default Blog
