import React from 'react';
import axios from 'axios';
import SocialMedia from '../components/SocialMedia.js';
import { 
  Link,
  HashRouter,
  Switch,
  Route
   } from 'react-router-dom';

class Blog extends React.Component {
  state = {
    posts: [],
    displayBlogBods: true
  };

  componentDidMount(){
    axios.get('https://api.peaua.io:8000/api/posts.json')
    .then(res => {
      const posts = res.data;
      this.setState({ posts });
    })
  }

  blogPostWrapper = () => {
    return (
      <div className='blog-post-wrapper'>
        <ul>
          { this.state.posts.map(post =>
          <div className='blog-post-container'>
            <li className='blog-post' key={ post.id }><Link to='/test'> { post.title }</Link></li> 
            <p className='published'> { post.published }</p>
            <div className='tag-cont'>
              <ul className='tag-list'>{ post.tags.map( tag =>
                <li className='tag'>{tag}</li>)}
              </ul>
            </div>
          </div> ) }
        </ul>
      </div>
    )

  }

  BlogPost = ( props ) => {
    return (

      <div>

      <h1> { props.title } </h1>
      <h2> { props.published } </h2>
      <p> { props.body } </p>

      </div>

    )
  }

  render() {
  	return (
      <HashRouter>
  	  <div className='blog-main'>
  	    <h1>Blog</h1>

        <div className='blog-wrapper'>

          <div className='social-media-wrapper'>
            <SocialMedia />
          </div>

          <Switch>
          <Route path='/' component={ this.blogPostWrapper } />
          <Route path='/test' component={ this.BlogPost(this.state.posts.id['1'])} />
          </Switch> 

        </div>

      </div>
      </HashRouter>	
  	)
  }

}

export default Blog;
