import React from 'react';
import axios from 'axios';
import SocialMedia from '../components/SocialMedia.js';
import { 
  Link,
  HashRouter,
  Switch,
  Route
   } from 'react-router-dom';
import BlogPost from './BlogPost.js';


class Blog extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
    posts: []
    };
  }

  componentDidMount(){
    axios.get('http://localhost:8000/api/posts.json')
    .then(res => {
      const posts = res.data;
      this.setState({ posts });
    })
  }

  blogPostWrapper = () => {
    return (
      <div className='blog-post-wrapper'>
        <ul>
          { this.state.posts.map(post =>
          <div className='blog-post-container'>
            <li className='blog-post' key={ post.id }><Link to={`/Blog/%{post.id}`}> { post.title }</Link></li> 
            <p className='published'> { post.published }</p>
            <div className='tag-cont'>
              <ul className='tag-list'>{ post.tags.map( tag =>
                <li className='tag'>{tag}</li>)}
              </ul>
            </div>
          </div> ) }
        </ul>
      </div>
    )

  }

  blogPost = () => {
    console.log(this.state.posts[0]['title'])
    return (

      <div>

        <h1> Test TEst</h1>
          <h2> 123 </h2>
        <p> this is a test </p>

      </div>

    )
  }

  render() {

    let posting;
    if(this.state.posts[0]) {
        posting = this.blogPost
    } else {
        posting = null
    }

  	return (
      <HashRouter>
  	  <div className='blog-main'>
  	    <h1>Blog</h1>

        <div className='blog-wrapper'>

          <div className='social-media-wrapper'>
            <SocialMedia />
          </div>

          <Switch>
          <Route exact path='/Blog/' component={ this.blogPostWrapper } />
          <Route exact path='/Blog/:postId/' render={ () => (<div><p>Test</p></div>)  } />
          </Switch> 

        </div>

      </div>
      </HashRouter>	
  	)
  }

}

export default Blog;
