import React from 'react';
import axios from 'axios';
import SocialMedia from '../components/SocialMedia.js';
import { 
  Link,
  HashRouter,
  Switch,
  Route
   } from 'react-router-dom';
import BlogPost from './BlogPost.js';


class Blog extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
    posts: []
    };
  }

  componentDidMount(){
    axios.get('https://api.peaua.io:8000/api/posts.json')
    .then(res => {
      const posts = res.data;
      this.setState({ posts });
    })
  }

  blogPostWrapper = () => {
    return (
      <div className='blog-post-wrapper'>
        <ul>
          { this.state.posts.map(post =>
          <div className='blog-post-container'>
            <li className='blog-post' key={ post.id }><Link to='/Blog/test'> { post.title }</Link></li> 
            <p className='published'> { post.published }</p>
            <div className='tag-cont'>
              <ul className='tag-list'>{ post.tags.map( tag =>
                <li className='tag'>{tag}</li>)}
              </ul>
            </div>
          </div> ) }
        </ul>
      </div>
    )

  }

  blogPost = (prop) => {
    console.log(prop)
    return (

      <div>

      <h1> { prop.title } </h1>
      <h2> { prop.published } </h2>
      <p> { prop.body } </p>

      </div>

    )
  }

  render() {
    if (this.state.posts[0] != '') {
      const posting = this.blogPost(this.state.posts[0]);
    } else {
      const posting = null;
    }
  	return (
      <HashRouter>
  	  <div className='blog-main'>
  	    <h1>Blog</h1>

        <div className='blog-wrapper'>

          <div className='social-media-wrapper'>
            <SocialMedia />
          </div>

          <Switch>
          <Route path='/' component={ this.blogPostWrapper } />
          <Route path='/Blog/test' component={ this.posting } />
          </Switch> 

        </div>

      </div>
      </HashRouter>	
  	)
  }

}

export default Blog;
